"use client"

import { useState, useRef, useEffect } from "react"
import "./VideoPlayer.css"

const VideoPlayer = ({ videoUrl, thumbnailUrl, title }) => {
  const videoRef = useRef(null)
  const [isPlaying, setIsPlaying] = useState(false)
  const [progress, setProgress] = useState(0)
  const [volume, setVolume] = useState(1)
  const [isMuted, setIsMuted] = useState(false)
  const [duration, setDuration] = useState(0)
  const [currentTime, setCurrentTime] = useState(0)
  const [isLoading, setIsLoading] = useState(true)
  const [hasError, setHasError] = useState(false)
  const [errorMessage, setErrorMessage] = useState("")
  const [showControls, setShowControls] = useState(true)

  // Debug logging
  useEffect(() => {
    console.log("VideoPlayer props:", { videoUrl, thumbnailUrl, title })
  }, [videoUrl, thumbnailUrl, title])

  // Format time display
  const formatTime = (time) => {
    if (!time || isNaN(time)) return "0:00"
    const minutes = Math.floor(time / 60)
    const seconds = Math.floor(time % 60)
    return `${minutes}:${seconds.toString().padStart(2, "0")}`
  }

  // Handle play/pause
  const togglePlay = async () => {
    if (!videoRef.current || hasError) return

    try {
      if (isPlaying) {
        videoRef.current.pause()
        setIsPlaying(false)
      } else {
        await videoRef.current.play()
        setIsPlaying(true)
      }
    } catch (error) {
      console.error("Error toggling play:", error)
      setHasError(true)
      setErrorMessage(`Playback error: ${error.message}`)
    }
  }

  // Handle time update
  const handleTimeUpdate = () => {
    if (videoRef.current) {
      const current = videoRef.current.currentTime
      const total = videoRef.current.duration

      setCurrentTime(current)
      if (total) {
        setProgress((current / total) * 100)
      }
    }
  }

  // Handle seeking
  const handleSeek = (e) => {
    if (!videoRef.current || !duration) return

    const rect = e.currentTarget.getBoundingClientRect()
    const clickX = e.clientX - rect.left
    const percentage = clickX / rect.width
    const newTime = percentage * duration

    videoRef.current.currentTime = newTime
    setCurrentTime(newTime)
    setProgress(percentage * 100)
  }

  // Handle volume change
  const handleVolumeChange = (e) => {
    const newVolume = Number.parseFloat(e.target.value)
    setVolume(newVolume)
    if (videoRef.current) {
      videoRef.current.volume = newVolume
    }
    setIsMuted(newVolume === 0)
  }

  // Toggle mute
  const toggleMute = () => {
    if (!videoRef.current) return

    if (isMuted) {
      videoRef.current.volume = volume
      setIsMuted(false)
    } else {
      videoRef.current.volume = 0
      setIsMuted(true)
    }
  }

  // Video event handlers
  const handleLoadStart = () => {
    console.log("Video loading started")
    setIsLoading(true)
    setHasError(false)
  }

  const handleLoadedMetadata = () => {
    console.log("Video metadata loaded")
    if (videoRef.current) {
      setDuration(videoRef.current.duration)
      console.log("Video duration:", videoRef.current.duration)
    }
  }

  const handleCanPlay = () => {
    console.log("Video can play")
    setIsLoading(false)
  }

  const handleError = (e) => {
    console.error("Video error:", e)
    const error = videoRef.current?.error
    let message = "Unknown error"

    if (error) {
      switch (error.code) {
        case error.MEDIA_ERR_ABORTED:
          message = "Video playback was aborted"
          break
        case error.MEDIA_ERR_NETWORK:
          message = "Network error occurred"
          break
        case error.MEDIA_ERR_DECODE:
          message = "Video decoding error"
          break
        case error.MEDIA_ERR_SRC_NOT_SUPPORTED:
          message = "Video format not supported"
          break
        default:
          message = error.message || "Unknown video error"
      }
    }

    console.error("Video error details:", {
      code: error?.code,
      message: error?.message,
      videoUrl,
    })

    setHasError(true)
    setErrorMessage(message)
    setIsLoading(false)
  }

  const handleEnded = () => {
    setIsPlaying(false)
    setProgress(0)
    setCurrentTime(0)
  }

  const handlePause = () => {
    setIsPlaying(false)
  }

  const handlePlay = () => {
    setIsPlaying(true)
  }

  // Auto-hide controls
  useEffect(() => {
    let timeout
    if (isPlaying && showControls) {
      timeout = setTimeout(() => {
        setShowControls(false)
      }, 3000)
    }

    return () => {
      if (timeout) clearTimeout(timeout)
    }
  }, [isPlaying, showControls])

  // Show controls on mouse move
  const handleMouseMove = () => {
    setShowControls(true)
  }

  // Reset state when video URL changes
  useEffect(() => {
    if (videoRef.current) {
      setIsPlaying(false)
      setProgress(0)
      setCurrentTime(0)
      setDuration(0)
      setIsLoading(true)
      setHasError(false)
      setErrorMessage("")
    }
  }, [videoUrl])

  if (hasError) {
    return (
      <div className="video-player video-player--error">
        <div className="video-player__error">
          <div className="video-player__error-icon">‚ö†Ô∏è</div>
          <h3>Unable to play video</h3>
          <p>{errorMessage}</p>
          <p className="video-player__error-url">URL: {videoUrl}</p>
          <button
            className="video-player__retry-btn"
            onClick={() => {
              setHasError(false)
              setIsLoading(true)
              if (videoRef.current) {
                videoRef.current.load()
              }
            }}
          >
            Try Again
          </button>
        </div>
      </div>
    )
  }

  return (
    <div className="video-player" onMouseMove={handleMouseMove} onMouseLeave={() => setShowControls(false)}>
      <video
        ref={videoRef}
        className="video-player__video"
        src={videoUrl}
        poster={thumbnailUrl}
        onLoadStart={handleLoadStart}
        onLoadedMetadata={handleLoadedMetadata}
        onCanPlay={handleCanPlay}
        onTimeUpdate={handleTimeUpdate}
        onError={handleError}
        onEnded={handleEnded}
        onPause={handlePause}
        onPlay={handlePlay}
        preload="metadata"
        crossOrigin="anonymous"
      />

      {isLoading && (
        <div className="video-player__loading">
          <div className="video-player__spinner"></div>
          <p>Loading video...</p>
        </div>
      )}

      {!isPlaying && !isLoading && !hasError && (
        <button className="video-player__play-overlay" onClick={togglePlay}>
          <div className="video-player__play-icon">‚ñ∂</div>
        </button>
      )}

      <div className={`video-player__controls ${showControls ? "video-player__controls--visible" : ""}`}>
        <div className="video-player__progress-container" onClick={handleSeek}>
          <div className="video-player__progress-bar">
            <div className="video-player__progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
        </div>

        <div className="video-player__controls-row">
          <div className="video-player__controls-left">
            <button className="video-player__control-btn" onClick={togglePlay} disabled={hasError}>
              {isPlaying ? "‚è∏" : "‚ñ∂"}
            </button>

            <div className="video-player__volume">
              <button className="video-player__control-btn" onClick={toggleMute}>
                {isMuted ? "üîá" : "üîä"}
              </button>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={isMuted ? 0 : volume}
                onChange={handleVolumeChange}
                className="video-player__volume-slider"
              />
            </div>

            <div className="video-player__time">
              {formatTime(currentTime)} / {formatTime(duration)}
            </div>
          </div>

          <div className="video-player__controls-right">
            <button className="video-player__control-btn">‚õ∂</button>
          </div>
        </div>
      </div>

      {/* Debug info in development */}
      {process.env.NODE_ENV === "development" && (
        <div className="video-player__debug">
          <small>
            URL: {videoUrl} | Loading: {isLoading ? "Yes" : "No"} | Error: {hasError ? "Yes" : "No"}
          </small>
        </div>
      )}
    </div>
  )
}

export default VideoPlayer
