"use client"

import { useState, useRef, useEffect } from "react"
import "./VideoPlayer.css"

const VideoPlayer = ({ video, onError }) => {
  const videoRef = useRef(null)
  const [isPlaying, setIsPlaying] = useState(false)
  const [progress, setProgress] = useState(0)
  const [volume, setVolume] = useState(1)
  const [isMuted, setIsMuted] = useState(false)
  const [isFullscreen, setIsFullscreen] = useState(false)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [videoUrl, setVideoUrl] = useState("")

  // Set up the video URL when the component mounts or video changes
  useEffect(() => {
    if (video && video.videoUrl) {
      // Check if the URL is already absolute
      if (video.videoUrl.startsWith("http")) {
        setVideoUrl(video.videoUrl)
      } else {
        // Otherwise, prepend the API URL
        const baseUrl = process.env.REACT_APP_API_URL || "http://localhost:5000"
        setVideoUrl(`${baseUrl}${video.videoUrl}`)
      }
      console.log("Video URL set to:", video.videoUrl)
    }
  }, [video])

  // Handle play/pause
  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause()
      } else {
        videoRef.current.play().catch((err) => {
          console.error("Error playing video:", err)
          setError(`Error playing video: ${err.message}`)
        })
      }
      setIsPlaying(!isPlaying)
    }
  }

  // Update progress bar as video plays
  const handleTimeUpdate = () => {
    if (videoRef.current) {
      const currentProgress = (videoRef.current.currentTime / videoRef.current.duration) * 100
      setProgress(currentProgress)
    }
  }

  // Handle seeking when user clicks on progress bar
  const handleSeek = (e) => {
    if (videoRef.current) {
      const progressBar = e.currentTarget
      const clickPosition = (e.clientX - progressBar.getBoundingClientRect().left) / progressBar.offsetWidth
      videoRef.current.currentTime = clickPosition * videoRef.current.duration
    }
  }

  // Handle volume change
  const handleVolumeChange = (e) => {
    const newVolume = Number.parseFloat(e.target.value)
    setVolume(newVolume)
    if (videoRef.current) {
      videoRef.current.volume = newVolume
    }
    setIsMuted(newVolume === 0)
  }

  // Toggle mute
  const toggleMute = () => {
    if (videoRef.current) {
      if (isMuted) {
        videoRef.current.volume = volume || 1
        setIsMuted(false)
      } else {
        videoRef.current.volume = 0
        setIsMuted(true)
      }
    }
  }

  // Toggle fullscreen
  const toggleFullscreen = () => {
    const videoContainer = document.querySelector(".video-player-container")

    if (!isFullscreen) {
      if (videoContainer.requestFullscreen) {
        videoContainer.requestFullscreen()
      } else if (videoContainer.webkitRequestFullscreen) {
        videoContainer.webkitRequestFullscreen()
      } else if (videoContainer.msRequestFullscreen) {
        videoContainer.msRequestFullscreen()
      }
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen()
      } else if (document.webkitExitFullscreen) {
        document.webkitExitFullscreen()
      } else if (document.msExitFullscreen) {
        document.msExitFullscreen()
      }
    }
  }

  // Update fullscreen state when fullscreen changes
  useEffect(() => {
    const handleFullscreenChange = () => {
      setIsFullscreen(document.fullscreenElement || document.webkitFullscreenElement || document.msFullscreenElement)
    }

    document.addEventListener("fullscreenchange", handleFullscreenChange)
    document.addEventListener("webkitfullscreenchange", handleFullscreenChange)
    document.addEventListener("msfullscreenchange", handleFullscreenChange)

    return () => {
      document.removeEventListener("fullscreenchange", handleFullscreenChange)
      document.removeEventListener("webkitfullscreenchange", handleFullscreenChange)
      document.removeEventListener("msfullscreenchange", handleFullscreenChange)
    }
  }, [])

  // Handle video loading events
  const handleLoadStart = () => {
    console.log("Video loading started")
    setLoading(true)
  }

  const handleCanPlay = () => {
    console.log("Video can play now")
    setLoading(false)
  }

  const handleLoadedData = () => {
    console.log("Video data loaded")
    setLoading(false)
  }

  const handleVideoError = (e) => {
    console.error("Video error:", e)
    const errorMessage = e.target.error
      ? `Error: ${e.target.error.code} - ${e.target.error.message || "Unknown error"}`
      : "Unknown video error"

    setError(errorMessage)
    setLoading(false)

    if (onError) {
      onError(errorMessage)
    }
  }

  // Debug video element properties
  useEffect(() => {
    if (videoRef.current) {
      console.log("Video element properties:", {
        src: videoRef.current.src,
        networkState: videoRef.current.networkState,
        readyState: videoRef.current.readyState,
        paused: videoRef.current.paused,
        ended: videoRef.current.ended,
        error: videoRef.current.error,
      })
    }
  }, [loading, error, videoUrl])

  return (
    <div className="video-player-container">
      {/* Show loading indicator */}
      {loading && (
        <div className="video-loading">
          <div className="spinner"></div>
          <p>Loading video...</p>
        </div>
      )}

      {/* Show error message if there's an error */}
      {error && (
        <div className="video-error">
          <p>{error}</p>
          <p>Video URL: {videoUrl}</p>
          <button
            onClick={() => {
              setError(null)
              if (videoRef.current) {
                videoRef.current.load()
              }
            }}
            className="retry-button"
          >
            Retry
          </button>
        </div>
      )}

      {/* Video element */}
      <video
        ref={videoRef}
        className="video-player"
        src={videoUrl}
        poster={video?.thumbnailUrl}
        onClick={togglePlay}
        onTimeUpdate={handleTimeUpdate}
        onLoadStart={handleLoadStart}
        onCanPlay={handleCanPlay}
        onLoadedData={handleLoadedData}
        onError={handleVideoError}
        onEnded={() => setIsPlaying(false)}
        crossOrigin="anonymous"
      />

      {/* Video controls */}
      <div className="video-controls">
        <div className="progress-container" onClick={handleSeek}>
          <div className="progress-bar" style={{ width: `${progress}%` }}></div>
        </div>

        <div className="controls-buttons">
          <button onClick={togglePlay} className="control-button">
            {isPlaying ? <i className="fas fa-pause"></i> : <i className="fas fa-play"></i>}
          </button>

          <div className="volume-container">
            <button onClick={toggleMute} className="control-button">
              {isMuted ? <i className="fas fa-volume-mute"></i> : <i className="fas fa-volume-up"></i>}
            </button>
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={isMuted ? 0 : volume}
              onChange={handleVolumeChange}
              className="volume-slider"
            />
          </div>

          <button onClick={toggleFullscreen} className="control-button">
            {isFullscreen ? <i className="fas fa-compress"></i> : <i className="fas fa-expand"></i>}
          </button>
        </div>
      </div>

      {/* Debug info (only in development) */}
      {process.env.NODE_ENV === "development" && (
        <div className="video-debug">
          <p>Video URL: {videoUrl}</p>
          <p>Loading: {loading ? "Yes" : "No"}</p>
          <p>Error: {error ? "Yes" : "No"}</p>
          {videoRef.current && (
            <>
              <p>Network State: {videoRef.current.networkState}</p>
              <p>Ready State: {videoRef.current.readyState}</p>
            </>
          )}
        </div>
      )}
    </div>
  )
}

export default VideoPlayer
