"use client"

import { useState, useRef, useEffect } from "react"
import "./VideoPlayer.css"

const VideoPlayer = ({ videoUrl, thumbnailUrl, title }) => {
  const [error, setError] = useState(null)
  const [isLoading, setIsLoading] = useState(true)
  const [videoReady, setVideoReady] = useState(false)
  const videoRef = useRef(null)

  // Construct video URL for port 5001
  const getVideoSrc = () => {
    if (!videoUrl) {
      console.error("No video URL provided")
      return ""
    }

    console.log("Original video URL:", videoUrl)

    // If it's already a full URL, use it
    if (videoUrl.startsWith("http")) {
      console.log("Using full URL:", videoUrl)
      return videoUrl
    }

    // Construct URL for local files
    const baseUrl = "http://localhost:5001"
    let fullUrl = ""

    if (videoUrl.startsWith("/uploads")) {
      fullUrl = `${baseUrl}${videoUrl}`
    } else if (videoUrl.startsWith("uploads")) {
      fullUrl = `${baseUrl}/${videoUrl}`
    } else {
      // Assume it's just a filename
      fullUrl = `${baseUrl}/uploads/videos/${videoUrl}`
    }

    console.log("Constructed video URL:", fullUrl)
    return fullUrl
  }

  const videoSrc = getVideoSrc()

  useEffect(() => {
    if (!videoSrc) {
      setError("No video source available")
      setIsLoading(false)
      return
    }

    // Test if the video URL is accessible
    fetch(videoSrc, { method: "HEAD" })
      .then((response) => {
        if (response.ok) {
          console.log("Video URL is accessible:", videoSrc)
          setVideoReady(true)
        } else {
          console.error("Video URL not accessible:", response.status, videoSrc)
          setError(`Video not found (${response.status})`)
        }
      })
      .catch((err) => {
        console.error("Error checking video URL:", err, videoSrc)
        setError("Cannot access video file")
      })
      .finally(() => {
        setIsLoading(false)
      })
  }, [videoSrc])

  const handleVideoError = (e) => {
    console.error("Video playback error:", e)
    const video = e.target
    if (video.error) {
      console.error("Video error details:", {
        code: video.error.code,
        message: video.error.message,
        src: video.src,
      })
    }
    setError("Cannot play this video")
    setIsLoading(false)
  }

  const handleVideoLoad = () => {
    console.log("Video loaded successfully")
    setIsLoading(false)
    setError(null)
  }

  const handleVideoLoadStart = () => {
    console.log("Video load started")
    setIsLoading(true)
  }

  if (error) {
    return (
      <div className="video-player-error">
        <div className="error-content">
          <div className="error-icon">⚠️</div>
          <h3>Video Unavailable</h3>
          <p>{error}</p>
          <div className="debug-info">
            <p>
              <strong>Video URL:</strong> {videoSrc}
            </p>
            <p>
              <strong>Original URL:</strong> {videoUrl}
            </p>
          </div>
          <button
            className="retry-button"
            onClick={() => {
              setError(null)
              setIsLoading(true)
              window.location.reload()
            }}
          >
            Refresh Page
          </button>
        </div>
      </div>
    )
  }

  if (isLoading) {
    return (
      <div className="video-player-loading">
        <div className="loading-content">
          <div className="loading-spinner"></div>
          <p>Loading video...</p>
          <p className="loading-url">Checking: {videoSrc}</p>
        </div>
      </div>
    )
  }

  if (!videoReady) {
    return (
      <div className="video-player-error">
        <div className="error-content">
          <div className="error-icon">📹</div>
          <h3>Video Not Ready</h3>
          <p>The video file could not be loaded.</p>
          <div className="debug-info">
            <p>
              <strong>URL:</strong> {videoSrc}
            </p>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="video-player-container">
      <video
        ref={videoRef}
        className="video-player"
        src={videoSrc}
        poster={thumbnailUrl}
        controls
        preload="metadata"
        onError={handleVideoError}
        onLoadedData={handleVideoLoad}
        onLoadStart={handleVideoLoadStart}
      >
        <source src={videoSrc} type="video/mp4" />
        <source src={videoSrc} type="video/webm" />
        <source src={videoSrc} type="video/ogg" />
        Your browser does not support the video tag.
      </video>
    </div>
  )
}

export default VideoPlayer
