"use client"

import { useState, useRef, useEffect } from "react"
import "./VideoPlayer.css"

const VideoPlayer = ({ videoUrl, thumbnailUrl, title }) => {
  const [isPlaying, setIsPlaying] = useState(false)
  const [currentTime, setCurrentTime] = useState(0)
  const [duration, setDuration] = useState(0)
  const [volume, setVolume] = useState(1)
  const [isLoading, setIsLoading] = useState(true)
  const [error, setError] = useState(null)
  const [showControls, setShowControls] = useState(true)

  const videoRef = useRef(null)
  const videoContainerRef = useRef(null)

  // Debug logging
  useEffect(() => {
    console.log("VideoPlayer received:", { videoUrl, thumbnailUrl, title })
  }, [videoUrl, thumbnailUrl, title])

  // Format time display
  const formatTime = (time) => {
    if (!time || isNaN(time)) return "0:00"
    const minutes = Math.floor(time / 60)
    const seconds = Math.floor(time % 60)
    return `${minutes}:${seconds.toString().padStart(2, "0")}`
  }

  // Handle play/pause
  const togglePlay = async () => {
    if (!videoRef.current || error) return

    try {
      if (isPlaying) {
        videoRef.current.pause()
      } else {
        await videoRef.current.play()
      }
    } catch (err) {
      console.error("Error toggling play:", err)
      setError(`Playback error: ${err.message}`)
    }
  }

  // Handle time update
  const handleTimeUpdate = () => {
    if (videoRef.current) {
      setCurrentTime(videoRef.current.currentTime)
    }
  }

  // Handle seeking
  const handleSeek = (e) => {
    if (!videoRef.current || !duration) return

    const rect = e.currentTarget.getBoundingClientRect()
    const clickX = e.clientX - rect.left
    const percentage = clickX / rect.width
    const newTime = percentage * duration

    videoRef.current.currentTime = newTime
    setCurrentTime(newTime)
  }

  // Handle volume change
  const handleVolumeChange = (e) => {
    const newVolume = Number.parseFloat(e.target.value)
    setVolume(newVolume)
    if (videoRef.current) {
      videoRef.current.volume = newVolume
    }
  }

  // Video event handlers
  const handleLoadStart = () => {
    console.log("Video loading started")
    setIsLoading(true)
    setError(null)
  }

  const handleLoadedMetadata = () => {
    console.log("Video metadata loaded")
    if (videoRef.current) {
      setDuration(videoRef.current.duration)
    }
  }

  const handleCanPlay = () => {
    console.log("Video can play")
    setIsLoading(false)
  }

  const handleError = (e) => {
    console.error("Video error:", e)
    const videoError = videoRef.current?.error
    let message = "Unable to load video"

    if (videoError) {
      switch (videoError.code) {
        case videoError.MEDIA_ERR_ABORTED:
          message = "Video playback was aborted"
          break
        case videoError.MEDIA_ERR_NETWORK:
          message = "Network error - check your connection"
          break
        case videoError.MEDIA_ERR_DECODE:
          message = "Video format not supported"
          break
        case videoError.MEDIA_ERR_SRC_NOT_SUPPORTED:
          message = "Video source not supported"
          break
        default:
          message = videoError.message || "Unknown video error"
      }
    }

    console.error("Video error details:", {
      code: videoError?.code,
      message: videoError?.message,
      videoUrl,
    })

    setError(message)
    setIsLoading(false)
  }

  const handlePlay = () => {
    setIsPlaying(true)
  }

  const handlePause = () => {
    setIsPlaying(false)
  }

  const handleEnded = () => {
    setIsPlaying(false)
    setCurrentTime(0)
  }

  // Prepare video URL
  const getVideoUrl = () => {
    if (!videoUrl) return ""

    // If it's already a full URL, return it
    if (videoUrl.startsWith("http")) {
      return videoUrl
    }

    // If it's a relative URL, prepend the base URL
    const baseUrl = process.env.REACT_APP_API_URL?.replace("/api", "") || "http://localhost:5000"

    // Ensure proper URL formatting
    if (videoUrl.startsWith("/")) {
      return `${baseUrl}${videoUrl}`
    } else {
      return `${baseUrl}/${videoUrl}`
    }
  }

  const fullVideoUrl = getVideoUrl()

  // Show error state
  if (error) {
    return (
      <div className="video-player video-player--error">
        <div className="video-player__error">
          <div className="video-player__error-icon">‚ö†Ô∏è</div>
          <h3>Unable to play video</h3>
          <p>{error}</p>
          <p className="video-player__error-url">URL: {fullVideoUrl}</p>
          <button
            className="video-player__retry-btn"
            onClick={() => {
              setError(null)
              setIsLoading(true)
              if (videoRef.current) {
                videoRef.current.load()
              }
            }}
          >
            Try Again
          </button>
        </div>
      </div>
    )
  }

  return (
    <div className="video-player" ref={videoContainerRef}>
      <video
        ref={videoRef}
        className="video-player__video"
        src={fullVideoUrl}
        poster={thumbnailUrl}
        onLoadStart={handleLoadStart}
        onLoadedMetadata={handleLoadedMetadata}
        onCanPlay={handleCanPlay}
        onTimeUpdate={handleTimeUpdate}
        onError={handleError}
        onPlay={handlePlay}
        onPause={handlePause}
        onEnded={handleEnded}
        preload="metadata"
        crossOrigin="anonymous"
        controls={false}
      />

      {isLoading && (
        <div className="video-player__loading">
          <div className="video-player__spinner"></div>
          <p>Loading video...</p>
        </div>
      )}

      {!isPlaying && !isLoading && !error && (
        <button className="video-player__play-overlay" onClick={togglePlay}>
          <div className="video-player__play-icon">‚ñ∂</div>
        </button>
      )}

      <div className={`video-player__controls ${showControls ? "video-player__controls--visible" : ""}`}>
        <div className="video-player__progress-container" onClick={handleSeek}>
          <div className="video-player__progress-bar">
            <div
              className="video-player__progress-fill"
              style={{ width: `${duration ? (currentTime / duration) * 100 : 0}%` }}
            ></div>
          </div>
        </div>

        <div className="video-player__controls-row">
          <div className="video-player__controls-left">
            <button className="video-player__control-btn" onClick={togglePlay}>
              {isPlaying ? "‚è∏" : "‚ñ∂"}
            </button>

            <div className="video-player__volume">
              <button className="video-player__control-btn">{volume === 0 ? "üîá" : "üîä"}</button>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={volume}
                onChange={handleVolumeChange}
                className="video-player__volume-slider"
              />
            </div>

            <div className="video-player__time">
              {formatTime(currentTime)} / {formatTime(duration)}
            </div>
          </div>
        </div>
      </div>

      {/* Debug info in development */}
      {process.env.NODE_ENV === "development" && (
        <div className="video-player__debug">
          <small>
            Original URL: {videoUrl}
            <br />
            Full URL: {fullVideoUrl}
            <br />
            Loading: {isLoading ? "Yes" : "No"} | Error: {error ? "Yes" : "No"}
          </small>
        </div>
      )}
    </div>
  )
}

export default VideoPlayer
