"use client"

// VideoPage.jsx - Individual video page component
// Displays video player, video info, comments, and related videos

import { useState, useEffect } from "react"
import { useParams, Link } from "react-router-dom"
import "./VideoPage.css"
import VideoPlayer from "../components/videos/VideoPlayer"
import CommentSection from "../components/comments/CommentSection"
import VideoCard from "../components/videos/VideoCard"
import {
  fetchVideoById,
  fetchVideoComments,
  addComment,
  likeVideo,
  subscribeToChannel,
  fetchRelatedVideos,
} from "../services/api"
import { useAuth } from "../context/AuthContext"
import { toast } from "react-toastify"
import { formatDistanceToNow } from "date-fns"

function VideoPage() {
  const { videoId } = useParams()
  const { currentUser } = useAuth()

  const [video, setVideo] = useState(null)
  const [comments, setComments] = useState([])
  const [relatedVideos, setRelatedVideos] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [showFullDescription, setShowFullDescription] = useState(false)
  const [isLiked, setIsLiked] = useState(false)
  const [isDisliked, setIsDisliked] = useState(false)
  const [isSubscribed, setIsSubscribed] = useState(false)
  const [likes, setLikes] = useState(0)
  const [dislikes, setDislikes] = useState(0)
  const [subscribers, setSubscribers] = useState(0)

  // Load video data on component mount or when videoId changes
  useEffect(() => {
    if (videoId) {
      loadVideoData()
    }
  }, [videoId])

  const loadVideoData = async () => {
    try {
      setLoading(true)
      setError(null)

      // Fetch video details
      const videoData = await fetchVideoById(videoId)
      setVideo(videoData)
      setLikes(videoData.likes || 0)
      setDislikes(videoData.dislikes || 0)
      setSubscribers(videoData.channel.subscribers || 0)

      // Fetch comments
      const commentsData = await fetchVideoComments(videoId)
      setComments(commentsData)

      // Fetch related videos
      const relatedData = await fetchRelatedVideos(videoId)
      setRelatedVideos(relatedData)

      // Check user's interaction status if logged in
      if (currentUser) {
        // These would be API calls to check user's status
        // setIsLiked(videoData.userLiked);
        // setIsDisliked(videoData.userDisliked);
        // setIsSubscribed(videoData.userSubscribed);
      }
    } catch (error) {
      console.error("Error loading video:", error)
      setError("Failed to load video. Please try again.")
      toast.error("Failed to load video")
    } finally {
      setLoading(false)
    }
  }

  // Handle like button click
  const handleLike = async () => {
    if (!currentUser) {
      toast.error("Please sign in to like videos")
      return
    }

    try {
      await likeVideo(videoId, !isLiked)

      if (isLiked) {
        setLikes(likes - 1)
        setIsLiked(false)
      } else {
        setLikes(likes + 1)
        setIsLiked(true)

        // Remove dislike if it was disliked
        if (isDisliked) {
          setDislikes(dislikes - 1)
          setIsDisliked(false)
        }
      }
    } catch (error) {
      console.error("Error liking video:", error)
      toast.error("Failed to like video")
    }
  }

  // Handle dislike button click
  const handleDislike = async () => {
    if (!currentUser) {
      toast.error("Please sign in to dislike videos")
      return
    }

    try {
      // This would be an API call to dislike the video
      if (isDisliked) {
        setDislikes(dislikes - 1)
        setIsDisliked(false)
      } else {
        setDislikes(dislikes + 1)
        setIsDisliked(true)

        // Remove like if it was liked
        if (isLiked) {
          setLikes(likes - 1)
          setIsLiked(false)
        }
      }
    } catch (error) {
      console.error("Error disliking video:", error)
      toast.error("Failed to dislike video")
    }
  }

  // Handle subscribe button click
  const handleSubscribe = async () => {
    if (!currentUser) {
      toast.error("Please sign in to subscribe")
      return
    }

    try {
      await subscribeToChannel(video.channel.id, !isSubscribed)

      if (isSubscribed) {
        setSubscribers(subscribers - 1)
        setIsSubscribed(false)
        toast.success("Unsubscribed")
      } else {
        setSubscribers(subscribers + 1)
        setIsSubscribed(true)
        toast.success("Subscribed!")
      }
    } catch (error) {
      console.error("Error subscribing:", error)
      toast.error("Failed to subscribe")
    }
  }

  // Handle adding a comment
  const handleAddComment = async (commentText) => {
    try {
      const newComment = await addComment(videoId, commentText)
      setComments([newComment, ...comments])
      toast.success("Comment added!")
    } catch (error) {
      console.error("Error adding comment:", error)
      throw error // Re-throw to be handled by CommentSection
    }
  }

  // Format view count
  const formatViewCount = (count) => {
    if (count >= 1000000) {
      return `${(count / 1000000).toFixed(1)}M`
    } else if (count >= 1000) {
      return `${(count / 1000).toFixed(1)}K`
    }
    return count.toString()
  }

  // Format subscriber count
  const formatSubscriberCount = (count) => {
    if (count >= 1000000) {
      return `${(count / 1000000).toFixed(1)}M`
    } else if (count >= 1000) {
      return `${(count / 1000).toFixed(1)}K`
    }
    return count.toString()
  }

  if (loading) {
    return (
      <div className="video-page">
        <div className="video-page__loading">
          <div className="video-page__loading-spinner"></div>
          <p>Loading video...</p>
        </div>
      </div>
    )
  }

  if (error || !video) {
    return (
      <div className="video-page">
        <div className="video-page__error">
          <p>{error || "Video not found"}</p>
          <button onClick={loadVideoData} className="video-page__retry-btn">
            Try Again
          </button>
        </div>
      </div>
    )
  }

  return (
    <div className="video-page">
      <div className="video-page__main">
        {/* Video player */}
        <div className="video-page__player">
          <VideoPlayer videoUrl={video.videoUrl} thumbnailUrl={video.thumbnailUrl} />
        </div>

        {/* Video info */}
        <div className="video-page__info">
          <h1 className="video-page__title">{video.title}</h1>

          <div className="video-page__metadata">
            <div className="video-page__views-date">
              {formatViewCount(video.views)} views •{" "}
              {formatDistanceToNow(new Date(video.createdAt), { addSuffix: true })}
            </div>

            <div className="video-page__actions">
              <button
                className={`video-page__action-btn ${isLiked ? "video-page__action-btn--active" : ""}`}
                onClick={handleLike}
              >
                <span className="video-page__action-icon">👍</span>
                {likes > 0 && <span>{formatViewCount(likes)}</span>}
              </button>

              <button
                className={`video-page__action-btn ${isDisliked ? "video-page__action-btn--active" : ""}`}
                onClick={handleDislike}
              >
                <span className="video-page__action-icon">👎</span>
              </button>

              <button className="video-page__action-btn">
                <span className="video-page__action-icon">📤</span>
                <span>Share</span>
              </button>

              <button className="video-page__action-btn">
                <span className="video-page__action-icon">⬇</span>
                <span>Download</span>
              </button>
            </div>
          </div>
        </div>

        {/* Channel info */}
        <div className="video-page__channel">
          <Link to={`/channel/${video.channel.id}`} className="video-page__channel-info">
            <img
              src={video.channel.profilePicture || "/placeholder.svg?height=48&width=48"}
              alt={video.channel.name}
              className="video-page__channel-avatar"
            />
            <div className="video-page__channel-details">
              <h3 className="video-page__channel-name">{video.channel.name}</h3>
              <p className="video-page__channel-subscribers">{formatSubscriberCount(subscribers)} subscribers</p>
            </div>
          </Link>

          <button
            className={`video-page__subscribe-btn ${isSubscribed ? "video-page__subscribe-btn--subscribed" : ""}`}
            onClick={handleSubscribe}
          >
            {isSubscribed ? "Subscribed" : "Subscribe"}
          </button>
        </div>

        {/* Video description */}
        <div className="video-page__description">
          <div
            className={`video-page__description-text ${showFullDescription ? "video-page__description-text--expanded" : ""}`}
          >
            {video.description}
          </div>

          {video.description && video.description.length > 200 && (
            <button
              className="video-page__description-toggle"
              onClick={() => setShowFullDescription(!showFullDescription)}
            >
              {showFullDescription ? "Show less" : "Show more"}
            </button>
          )}
        </div>

        {/* Comments section */}
        <CommentSection videoId={videoId} comments={comments} onAddComment={handleAddComment} />
      </div>

      {/* Related videos sidebar */}
      <div className="video-page__sidebar">
        <h3 className="video-page__sidebar-title">Related videos</h3>
        <div className="video-page__related-videos">
          {relatedVideos.map((relatedVideo) => (
            <VideoCard key={relatedVideo.id} video={relatedVideo} channelData={relatedVideo.channel} layout="list" />
          ))}
        </div>
      </div>
    </div>
  )
}

export default VideoPage
