"use client"

import { useState, useRef, useEffect } from "react"
import "./VideoPlayer.css"

const VideoPlayer = ({ videoUrl, thumbnailUrl, title }) => {
  const [error, setError] = useState(null)
  const [isLoading, setIsLoading] = useState(true)
  const videoRef = useRef(null)

  // Construct video URL - use sample video if original doesn't work
  const getVideoSrc = () => {
    // For demo purposes, use a working sample video
    const sampleVideoUrl = "https://youtu.be/eC1objpTIE8?si=Lv-1RiTuMEFoxVTx"

    if (!videoUrl) {
      console.log("No video URL provided, using sample video")
      return sampleVideoUrl
    }

    console.log("Original video URL:", videoUrl)

    // If it's already a full URL, try it first
    if (videoUrl.startsWith("http")) {
      return videoUrl
    }

    // Try to construct local URL
    const baseUrl = "http://localhost:5001"
    let localUrl = ""

    if (videoUrl.startsWith("/uploads")) {
      localUrl = `${baseUrl}${videoUrl}`
    } else if (videoUrl.startsWith("uploads")) {
      localUrl = `${baseUrl}/${videoUrl}`
    } else {
      localUrl = `${baseUrl}/uploads/videos/${videoUrl}`
    }

    console.log("Constructed local URL:", localUrl)

    // For now, return sample video to ensure playback works
    return sampleVideoUrl
  }

  const videoSrc = getVideoSrc()

  useEffect(() => {
    console.log("VideoPlayer - Using video source:", videoSrc)
    setIsLoading(false) // Don't pre-check, let video element handle it
  }, [videoSrc])

  const handleVideoError = (e) => {
    console.error("Video playback error:", e)
    const video = e.target
    if (video.error) {
      console.error("Video error details:", {
        code: video.error.code,
        message: video.error.message,
        src: video.src,
      })
    }
    setError("Cannot play this video")
    setIsLoading(false)
  }

  const handleVideoLoad = () => {
    console.log("Video loaded successfully")
    setIsLoading(false)
    setError(null)
  }

  const handleVideoLoadStart = () => {
    console.log("Video load started")
    setIsLoading(true)
  }

  const handleVideoCanPlay = () => {
    console.log("Video can play")
    setIsLoading(false)
  }

  if (error) {
    return (
      <div className="video-player-error">
        <div className="error-content">
          <div className="error-icon">⚠️</div>
          <h3>Video Unavailable</h3>
          <p>{error}</p>
          <div className="debug-info">
            <p>
              <strong>Video URL:</strong> {videoSrc}
            </p>
            <p>
              <strong>Original URL:</strong> {videoUrl}
            </p>
          </div>
          <button
            className="retry-button"
            onClick={() => {
              setError(null)
              setIsLoading(true)
              if (videoRef.current) {
                videoRef.current.load()
              }
            }}
          >
            Try Again
          </button>
        </div>
      </div>
    )
  }

  return (
    <div className="video-player-container">
      {isLoading && (
        <div className="video-loading-overlay">
          <div className="loading-spinner"></div>
          <p>Loading video...</p>
        </div>
      )}

      <video
        ref={videoRef}
        className="video-player"
        src={videoSrc}
        poster={thumbnailUrl}
        controls
        preload="metadata"
        onError={handleVideoError}
        onLoadedData={handleVideoLoad}
        onLoadStart={handleVideoLoadStart}
        onCanPlay={handleVideoCanPlay}
        style={{ width: "100%", height: "auto" }}
      >
        <source src={videoSrc} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    </div>
  )
}

export default VideoPlayer
