"use client"

// VideoPlayer.jsx - Custom video player component
// Handles video playback with custom controls

import { useState, useRef, useEffect } from "react"
import "./VideoPlayer.css"

function VideoPlayer({ videoUrl, thumbnailUrl, onTimeUpdate }) {
  const [isPlaying, setIsPlaying] = useState(false)
  const [volume, setVolume] = useState(1)
  const [isMuted, setIsMuted] = useState(false)
  const [currentTime, setCurrentTime] = useState(0)
  const [duration, setDuration] = useState(0)
  const [showControls, setShowControls] = useState(true)
  const [isFullscreen, setIsFullscreen] = useState(false)
  const [showSettings, setShowSettings] = useState(false)
  const [playbackRate, setPlaybackRate] = useState(1)

  const videoRef = useRef(null)
  const playerRef = useRef(null)
  const controlsTimeoutRef = useRef(null)

  // Format time in MM:SS format
  const formatTime = (timeInSeconds) => {
    const minutes = Math.floor(timeInSeconds / 60)
    const seconds = Math.floor(timeInSeconds % 60)
    return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`
  }

  // Handle play/pause
  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause()
      } else {
        videoRef.current.play()
      }
      setIsPlaying(!isPlaying)
    }
  }

  // Handle volume change
  const handleVolumeChange = (e) => {
    const newVolume = Number.parseFloat(e.target.value)
    setVolume(newVolume)
    videoRef.current.volume = newVolume
    setIsMuted(newVolume === 0)
  }

  // Handle mute toggle
  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !isMuted
      setIsMuted(!isMuted)
    }
  }

  // Handle seeking
  const handleSeek = (e) => {
    const seekTime = (e.target.value / 100) * duration
    setCurrentTime(seekTime)
    videoRef.current.currentTime = seekTime
  }

  // Handle fullscreen toggle
  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      playerRef.current.requestFullscreen().catch((err) => {
        console.error(`Error attempting to enable fullscreen: ${err.message}`)
      })
    } else {
      document.exitFullscreen()
    }
  }

  // Handle playback rate change
  const changePlaybackRate = (rate) => {
    setPlaybackRate(rate)
    videoRef.current.playbackRate = rate
    setShowSettings(false)
  }

  // Show/hide controls based on mouse movement
  const handleMouseMove = () => {
    setShowControls(true)

    // Clear existing timeout
    if (controlsTimeoutRef.current) {
      clearTimeout(controlsTimeoutRef.current)
    }

    // Set new timeout to hide controls after 3 seconds
    controlsTimeoutRef.current = setTimeout(() => {
      if (isPlaying) {
        setShowControls(false)
      }
    }, 3000)
  }

  // Update time as video plays
  useEffect(() => {
    const video = videoRef.current

    const handleTimeUpdate = () => {
      setCurrentTime(video.currentTime)
      if (onTimeUpdate) {
        onTimeUpdate(video.currentTime)
      }
    }

    const handleLoadedMetadata = () => {
      setDuration(video.duration)
    }

    const handleEnded = () => {
      setIsPlaying(false)
    }

    const handleFullscreenChange = () => {
      setIsFullscreen(!!document.fullscreenElement)
    }

    video.addEventListener("timeupdate", handleTimeUpdate)
    video.addEventListener("loadedmetadata", handleLoadedMetadata)
    video.addEventListener("ended", handleEnded)
    document.addEventListener("fullscreenchange", handleFullscreenChange)

    return () => {
      video.removeEventListener("timeupdate", handleTimeUpdate)
      video.removeEventListener("loadedmetadata", handleLoadedMetadata)
      video.removeEventListener("ended", handleEnded)
      document.removeEventListener("fullscreenchange", handleFullscreenChange)

      if (controlsTimeoutRef.current) {
        clearTimeout(controlsTimeoutRef.current)
      }
    }
  }, [onTimeUpdate])

  return (
    <div
      className={`video-player ${isFullscreen ? "video-player--fullscreen" : ""}`}
      ref={playerRef}
      onMouseMove={handleMouseMove}
      onClick={togglePlay}
    >
      <video
        ref={videoRef}
        src={videoUrl}
        poster={thumbnailUrl}
        className="video-player__video"
        onClick={(e) => e.stopPropagation()}
      />

      {/* Play button overlay (shown when paused) */}
      {!isPlaying && (
        <button className="video-player__play-overlay" onClick={togglePlay}>
          <span className="video-player__play-icon">▶</span>
        </button>
      )}

      {/* Video controls */}
      <div
        className={`video-player__controls ${showControls ? "video-player__controls--visible" : ""}`}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Progress bar */}
        <div className="video-player__progress">
          <input
            type="range"
            min="0"
            max="100"
            value={(currentTime / duration) * 100 || 0}
            onChange={handleSeek}
            className="video-player__progress-bar"
          />
        </div>

        <div className="video-player__controls-bottom">
          {/* Left controls */}
          <div className="video-player__controls-left">
            <button className="video-player__control-button" onClick={togglePlay}>
              <span className="video-player__control-icon">{isPlaying ? "⏸" : "▶"}</span>
            </button>

            <div className="video-player__volume-container">
              <button className="video-player__control-button" onClick={toggleMute}>
                <span className="video-player__control-icon">{isMuted ? "🔇" : "🔊"}</span>
              </button>
              <input
                type="range"
                min="0"
                max="1"
                step="0.01"
                value={isMuted ? 0 : volume}
                onChange={handleVolumeChange}
                className="video-player__volume-slider"
              />
            </div>

            <div className="video-player__time">
              {formatTime(currentTime)} / {formatTime(duration)}
            </div>
          </div>

          {/* Right controls */}
          <div className="video-player__controls-right">
            <div className="video-player__settings-container">
              <button className="video-player__control-button" onClick={() => setShowSettings(!showSettings)}>
                <span className="video-player__control-icon">⚙</span>
              </button>

              {showSettings && (
                <div className="video-player__settings-menu">
                  <div className="video-player__settings-header">Playback speed</div>
                  {[0.25, 0.5, 0.75, 1, 1.25, 1.5, 1.75, 2].map((rate) => (
                    <button
                      key={rate}
                      className={`video-player__settings-item ${playbackRate === rate ? "video-player__settings-item--active" : ""}`}
                      onClick={() => changePlaybackRate(rate)}
                    >
                      {rate === 1 ? "Normal" : `${rate}x`}
                    </button>
                  ))}
                </div>
              )}
            </div>

            <button className="video-player__control-button" onClick={toggleFullscreen}>
              <span className="video-player__control-icon">⛶</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default VideoPlayer
